import React from 'react'
import axios from 'axios'
import styled from 'styled-components'




class BoredAPI extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            currentActivity: undefined, //buscar a ultima coisa que procurou
            numberOfParticipants: 2,
        }
    }

    handleChangeParticipants = (event) => {
        const newParticipantsCount= event.target.value;
        this.setState({ numberOfParticipants :newParticipantsCount})
    }

    getActivity = async () => {
        const activity = await axios.get(`http://www.boredapi.com/api/activity?participants=${this.state.numberOfParticipants}`);

        this.setState({ currentActivity : activity});
    }

    render(){
        return(
            <div>
                <input type ="number" value={this.state.numberOfParticipants} onChange={this.handleChangeParticipants}/>

                <button onClick={this.getActivity}>Pr√≥xima Atividade</button>
            </div>

        )
    }
}





export default BoredAPI
